import React, { useEffect, useMemo } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getCart } from "../../actions/cart";
import { Box, Button, Grid, Paper, Typography } from "@material-ui/core";
import CartProduct from "./CartProduct/CartProduct";

const Cart = () => {
  const dispatch = useDispatch();
  const user = useMemo(() => JSON.parse(localStorage.getItem("profile")), []);
  const cart = useSelector((state) => state.cart);

  useEffect(() => {
    if (user) {
      dispatch(getCart(user?.result?._id));
    }
  }, [dispatch, user]);

  const calculateSubtotal = (price, quantity) => {
    return price * quantity;
  };

  const calculateGrandTotal = (products) => {
    return products.reduce(
      (total, item) => total + item.product.price * item.quantity,
      0
    );
  };

  const grandTotal = useMemo(
    () => calculateGrandTotal(cart.products),
    [cart.products]
  );

  return (
    <div>
      <Typography variant="h4">Your Cart</Typography>
      <Grid container alignItems="stretch" spacing={3}>
        {cart.products.map((item) => (
          <Box sx={{ width: "100%" }}>
            <Grid key={item.product._id} item xs={12} sm={3} md={2}>
              <CartProduct item={item} product={item.product}></CartProduct>
              <Paper sx={{ backgroundColor: "white", width: "100%", mt: 1 }}>
                <Typography variant="body2">
                  Subtotal : $
                  {calculateSubtotal(item.product.price, item.quantity).toFixed(
                    2
                  )}
                </Typography>
              </Paper>
            </Grid>
          </Box>
        ))}
      </Grid>
      <Typography variant="h5" sx={{ mt: 1 }}>
        Grand Total : ${grandTotal.toFixed(2)}
      </Typography>
    </div>
  );
};

export default Cart;

