import React, { useState, useEffect, useMemo } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Typography, Box, Grid, CircularProgress } from "@material-ui/core";
import { getOrderSummary } from "../../actions/order";
import OrderItem from "./OrderItem/OrderItem";

function Order() {
  const user = useMemo(() => JSON.parse(localStorage.getItem("profile")), []);
  const dispatch = useDispatch();
  const orders = useSelector((state) => state.summary);
  const [loading, setLoading] = useState(true);
  let grandTotal = 0;

  useEffect(() => {
    dispatch(getOrderSummary(user?.result?._id)).then(() => setLoading(false));
    console.log(orders.products);
  }, [dispatch, user]);

  if (loading) {
    return <CircularProgress />;
  }

  return (
    <div>
      <Typography variant="h4">Your Orders</Typography>
      <Grid container spacing={3}>
        {orders[0].products.map((order) => (
          <Grid item key={order._id} xs={12} sm={6} md={4}>
            <OrderItem item={order} />
          </Grid>
        ))}
      </Grid>
      <Box>
        <Typography variant="h5">{orders.grandTotal}</Typography>
      </Box>
    </div>
  );
}

export default Order;
