import React, { useState } from "react";
import { AUTH } from "../../constants/actionTypes";
import { GoogleLogin, googleLogout } from "@react-oauth/google";
import { useHistory } from "react-router-dom";
import {
  Avatar,
  Button,
  Typography,
  Paper,
  Grid,
  Container,
} from "@material-ui/core";
import Input from "./Input";
import LockOutlinedIcon from "@material-ui/icons/LockOutlined";
import useStyles from "./styles";
import jwt_decode from "jwt-decode";
import { signup, signin } from "../../actions/auth";
import { useDispatch } from "react-redux";

//* for formData validation
import { validate } from "../../creadentialValidation/validation";
import Modal from "../Modal/Modal";

// initial variable
const initialState = {
  firstName: "",
  lastName: "",
  email: "",
  password: "",
  confirmPassword: "",
};

const Auth = () => {
  const classes = useStyles();
  const [isSignup, setIsSignup] = useState(false);

  //state for signin and signup
  const [formData, setFormData] = useState(initialState);

  const [showPassword, setShowPassword] = useState(false);

  const [isOpen, setIsOpen] = useState(false);

  const [errobj, setErrorObj] = useState({ errorStatus: false, message: "" });

  const dispatch = useDispatch();

  const history = useHistory();

  let obj = {};
  let actionObj = {};

  const handleSubmit = async (e) => {
    e.preventDefault();

    obj = validate(formData, isSignup);
    setErrorObj((errobj) => ({
      ...errobj,
      ...obj,
    }));

    if (obj.errorStatus) {
      setIsOpen(true);
    } else {
      if (isSignup) {
        // validate before checking and alert that error
        //! checking if action executed smoothly...
        actionObj = await dispatch(signup(formData, history));

        if (actionObj?.message) {
          //! this code executes if there is an issue in login
          setErrorObj((errobj) => ({
            ...errobj,
            message: actionObj.message,
          }));

          setIsOpen(true);
        } else {
          //! this code executes if there is no issue in auth...
          setErrorObj((errobj) => ({
            ...errobj,
            message: "Yay ! You Registered successfully!",
          }));
          console.log(errobj, "here");
          setIsOpen(true);
        }
      } else {
        //! checking if action executed smoothly...
        actionObj = await dispatch(signin(formData, history));

        if (actionObj?.message) {
          //! this code executes if there is an issue in login
          setErrorObj((errobj) => ({
            ...errobj,
            message: actionObj.message,
          }));

          setIsOpen(true);
        } else {
          //! this code executes if there is no issue in auth...
          setErrorObj((errobj) => ({
            ...errobj,
            message: "Login successful",
          }));

          console.log(errobj, "here");
          //! enables the modal
          setIsOpen(true);
        }
      }
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleShowPassword = () => {
    //! whenever you are changing the state using its previous state, then you need to use callback function with the prev state as its argument...
    setShowPassword((prevShowPassword) => !prevShowPassword);
  };

  const switchMode = () => {
    setIsSignup((prevIsSignup) => !prevIsSignup);
    setShowPassword(false);
  };

  const googleSuccess = async (res) => {
    console.log(jwt_decode(res?.credential));
    const token = res?.credential;
    const result = jwt_decode(res?.credential);

    try {
      dispatch({ type: AUTH, data: { result, token } });
      history.push("/");
    } catch (error) {
      console.log(error);
    }
  };

  const googleFailure = () => {};

  return (
    <Container component="main" maxWidth="xs">
      <Paper className={classes.paper} elevation={3}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography variant="h5">{isSignup ? "Register" : "Login"}</Typography>
        <form className={classes.form} onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            {isSignup && (
              <>
                <Input
                  name="firstName"
                  label="First Name"
                  handleChange={handleChange}
                  autoFocus
                  half
                />
                <Input
                  name="lastName"
                  label="Last Name"
                  handleChange={handleChange}
                  half
                />
              </>
            )}
            <Input
              name="email"
              label="Email Address"
              handleChange={handleChange}
              type="email"
            />
            <Input
              name="password"
              label="Password"
              handleChange={handleChange}
              type={showPassword ? "text" : "password"}
              handleShowPassword={handleShowPassword}
            />
            {isSignup && (
              <Input
                name="confirmPassword"
                label="Confirm Password"
                handleChange={handleChange}
                type="password"
              />
            )}
          </Grid>
          <Button
            className={classes.submit}
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
          >
            {isSignup ? "Register" : "Login"}
          </Button>
          <GoogleLogin
            render={(renderProps) => (
              <Button
                className={classes.googleButton}
                color="primary"
                fullWidth
                onClick={renderProps.onClick}
                disabled={renderProps.disabled}
                variant="contained"
              >
                Google Login
              </Button>
            )}
            onSuccess={googleSuccess}
            onError={googleFailure}
          />
          <Grid container justifyContent="flex-end">
            <Grid item>
              <Button onClick={switchMode}>
                {isSignup
                  ? "Already have an account ?, Login"
                  : "Dont have an account ?, Register"}
              </Button>
            </Grid>
          </Grid>
        </form>
      </Paper>
      <Modal open={isOpen} onClose={() => setIsOpen(false)}>
        {errobj.message}
      </Modal>
    </Container>
  );
};

export default Auth;
