import React, { useEffect, useMemo } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getCart, removeCartItem, updateCartItem } from "../../actions/cart";
import { Button, Grid, Typography } from "@material-ui/core";
import CartProduct from "./CartProduct/CartProduct";

const Cart = () => {
  const dispatch = useDispatch();
  const user = useMemo(() => JSON.parse(localStorage.getItem("profile")), []);
  const cart = useSelector((state) => state.cart);

  useEffect(() => {
    if (user) {
      dispatch(getCart(user?.result?._id));
    }
  }, [dispatch, user]);

  const handleRemove = (productId) => {
    dispatch(removeCartItem(user?.result?._id, productId));
  };

  const handleUpdateQuantity = (productId, quantity) => {
    dispatch(updateCartItem(user?.result?._id, productId, quantity));
  };

  return (
    <div>
      <Typography variant="h4">Your Cart</Typography>
      {cart.products.map((item) => (
        <Grid key={item.product._id} item xs={12} sm={6} md={4}>
          <CartProduct product={item.product}></CartProduct>
        </Grid>
      ))}
    </div>
  );
};

export default Cart;

