import React, { useEffect, useMemo } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getCart, removeCartItem, updateCartItem } from "../../actions/cart";
import {
  Button,
  Typography,
  Card,
  CardActions,
  CardContent,
  CardMedia,
} from "@material-ui/core";
import DeleteIcon from "@material-ui/icons/Delete";
import useStyles from "./styles";

const Cart = () => {
  const dispatch = useDispatch();
  const classes = useStyles();
  const user = useMemo(() => JSON.parse(localStorage.getItem("profile")), []);
  const cart = useSelector((state) => state.cart);

  useEffect(() => {
    if (user) {
      dispatch(getCart(user?.result?._id));
    }
  }, [dispatch, user]);

  const handleRemove = (productId) => {
    dispatch(removeCartItem(user?.result?._id, productId));
  };

  const handleUpdateQuantity = (productId, quantity) => {
    dispatch(updateCartItem(user?.result?._id, productId, quantity));
  };

  return (
    <div>
      <Typography variant="h4">Your Cart</Typography>
      {cart.products.map((item) => (
        <Card className={classes.card} key={item.product._id}>
          <CardMedia
            className={classes.media}
            image={item.product.selectedFile}
            title={item.product.title}
          />
          <div className={classes.overlay}>
            <Typography variant="h6">{item.product.title}</Typography>
          </div>
          <CardContent className={classes.cardContent}>
            <Typography variant="body2">Quantity: {item.quantity}</Typography>
          </CardContent>
          <CardActions className={classes.actions}>
            <Button
              onClick={() =>
                handleUpdateQuantity(item.product._id, item.quantity + 1)
              }
            >
              +
            </Button>
            <Button
              onClick={() =>
                handleUpdateQuantity(item.product._id, item.quantity - 1)
              }
            >
              -
            </Button>
            <Button
              size="small"
              color="secondary"
              onClick={() => handleRemove(item.product._id)}
            >
              <DeleteIcon fontSize="small" />
              Remove
            </Button>
          </CardActions>
        </Card>
      ))}
    </div>
  );
};

export default Cart;
